import { Component, OnInit } from '@angular/core';
import { MapService } from './service/map.service';
import { AdventureService } from '../adventurer/service/adventurer.service';

@Component({
  selector: 'app-map',
  templateUrl: './map.component.html',
  styleUrls: ['./map.component.css']
})
export class MapComponent implements OnInit {

  map!: any[][];
  adventurers!: any[];

  constructor(
    private mapService: MapService,
    private adventureService: AdventureService
  ) { }

  ngOnInit(): void {
    this.map = this.mapService.getMap();
    this.adventurers = this.adventureService.getAdventurers();
  }

}
<div *ngFor="let row of map; let y = index">
    <div *ngFor="let cell of row; let x = index">
        <div *ngIf="cell === 'P'">.</div>
        <div *ngIf="cell === 'M'">M</div>
        <div *ngIf="cell?.type === 'T'">T({{ cell.treasures }})</div>
        <div *ngFor="let adventurer of adventurers">
            <div *ngIf="adventurer.x === x && adventurer.y === y">
                {{ adventurer.name }}
            </div>
        </div>
    </div>
</div>
