import { Component } from '@angular/core';

@Component({
  selector: 'app-map',
  templateUrl: './map.component.html',
  styleUrls: ['./map.component.css']
})
export class MapComponent {

}
<div *ngFor="let row of map; let y = index">
  <div *ngFor="let cell of row; let x = index">
    <div *ngIf="cell === 'P'">.</div>
    <div *ngIf="cell === 'M'">M</div>
    <div *ngIf="cell?.type === 'T'">T({{cell.treasures}})</div>
    <div *ngFor="let adventurer of adventurers">
      <div *ngIf="adventurer.x === x && adventurer.y === y">{{adventurer.name}}</div>
    </div>
  </div>
</div>
