import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class MapService {

  map!: any[][];

  constructor() {
    // Appelons la fonction initializeMap dans le constructeur
    this.initializeMap(3, 4); // 3: largeur, 4: hauteur
  }

  initializeMap(width: number, height: number) {
    this.map = new Array(height).fill(null).map(() => new Array(width).fill('P'));

    // Ajoutons quelques montagnes et trésors pour tester
    this.map[1][1] = 'M'; // M pour Montagne
    this.map[2][2] = 'M'; // Une autre Montagne
    this.map[0][3] = { type: 'T', treasures: 2 }; // T pour Trésor avec 2 trésors
    this.map[1][3] = { type: 'T', treasures: 1 }; // Un autre Trésor avec 1 trésor
  }

  getMap() {
    return this.map;
  }
}
